"use strict";

import { Wordlist } from "./wordlist";
const words = "";
let wordlist = null;

function loadWords(lang) {
  if (wordlist != null) {
    return;
  }

  wordlist = words.replace(/([A-Z])/g, " $1").toLowerCase().substring(1).split(" "); // Verify the computed list matches the official list

  /* istanbul ignore if */

  if (Wordlist.check(lang) !== "0x5c1362d88fd4cf614a96f3234941d29f7d37c08c5292fde03bf62c2db6ff7620") {
    wordlist = null;
    throw new Error("BIP39 Wordlist for it (Italian) FAILED");
  }
}

class LangIt extends Wordlist {
  constructor() {
    super("it");
  }

  getWord(index) {
    loadWords(this);
    return wordlist[index];
  }

  getWordIndex(word) {
    loadWords(this);
    return wordlist.indexOf(word);
  }

}

const langIt = new LangIt();
Wordlist.register(langIt);
export { langIt };
